import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const questions = [
    "What was your childhood best friend’s nickname?",
    "In which city did your parents meet?",
    "What’s your neighbor’s last name?",
    "How many pets did you have at 10 years old?",
    "What month did you get married?"
];

const App = () => {
    const [selectedQuestions, setSelectedQuestions] = useState([]);
    const [answers, setAnswers] = useState({});

    const handleQuestionChange = (e) => {
        const { options } = e.target;
        const selected = [];
        for (let i = 0, l = options.length; i < l; i++) {
            if (options[i].selected) {
                selected.push(options[i].value);
            }
        }
        setSelectedQuestions(selected);
    };

    const handleAnswerChange = (index, e) => {
        const newAnswers = { ...answers };
        newAnswers[selectedQuestions[index]] = e.target.value;
        setAnswers(newAnswers);
    };

    const handleSubmit = async () => {
        try {
            const response = await axios.post('http://localhost:5000/save', { answers });
            alert(response.data);
        } catch (error) {
            console.error('There was an error saving the data!', error);
            alert('Failed to save data');
        }
    };

    return (
        <div className="App">
            <h1>Security Questions</h1>
            <div>
                <label htmlFor="question-select">Choose three questions:</label>
                <select id="question-select" multiple={true} value={selectedQuestions} onChange={handleQuestionChange}>
                    {questions.map((question, index) => (
                        <option key={index} value={question}>{question}</option>
                    ))}
                </select>
            </div>
            {selectedQuestions.map((question, index) => (
                <div key={index}>
                    <label>{question}</label>
                    <input
                        type="text"
                        value={answers[question] || ''}
                        onChange={(e) => handleAnswerChange(index, e)}
                    />
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
}

export default App;
