import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
    const [name, setName] = useState('');
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [selectedQuestions, setSelectedQuestions] = useState([]);
    const [submitted, setSubmitted] = useState(false);

    const questions = [
        "What was your childhood best friend’s nickname?",
        "In which city did your parents meet?",
        "What’s your neighbor’s last name?",
        "How many pets did you have at 10 years old?",
        "What month did you get married?"
    ];

    const handleQuestionChange = (index, event) => {
        const newQuestions = [...selectedQuestions];
        newQuestions[index] = event.target.value;
        setSelectedQuestions(newQuestions.filter(Boolean));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (selectedQuestions.length !== 3) {
            alert("Please select exactly 3 questions.");
            return;
        }

        const userDetails = {
            name,
            phone,
            password,
            questions: selectedQuestions
        };

        axios.post('http://localhost:5000/save', userDetails)
            .then(response => {
                console.log(response.data);
                setSubmitted(true);
            })
            .catch(error => {
                console.error('There was an error saving the data!', error);
            });
    };

    return (
        <div className="App">
            <header className="App-header">
                <h2>Enter your details</h2>
                <form onSubmit={handleSubmit}>
                    <label>
                        Name:
                        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
                    </label><br /><br />
                    <label>
                        Phone Number:
                        <input type="text" value={phone} onChange={(e) => setPhone(e.target.value)} />
                    </label><br /><br />
                    <label>
                        Password:
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                    </label><br /><br />
                    {Array.from({ length: 3 }, (_, i) => (
                        <div key={i}>
                            <label>
                                Select Question {i + 1}:
                                <select onChange={(e) => handleQuestionChange(i, e)} defaultValue="">
                                    <option value="" disabled>Select a question</option>
                                    {questions.map((question, index) => (
                                        <option key={index} value={question}>{question}</option>
                                    ))}
                                </select>
                            </label><br /><br />
                        </div>
                    ))}
                    <button type="submit">Submit</button>
                </form>
                {submitted && (
                    <div>
                        <h2>Data saved successfully!</h2>
                    </div>
                )}
            </header>
        </div>
    );
}

export default App;
